meta.script:
window.dataLayer = window.dataLayer || [];
function gtag(){dataLayer.push(arguments);}
gtag('js', new Date());
gtag('config', 'G-MENBM6GSNY');

body.script:
var gameLoaded = false;
var lockedOccured = false;
var gameJsonUrl = "https://googleusercontent.b-cdn.net/one/WebGL.json";

// Unity Progress
function UnityProgress(gameInstance, progress) {
  if (!gameInstance.Module) return;
  const loader = document.querySelector("#loader");
  if (!gameInstance.progress) {
    const progressEl = loader.querySelector(".progress .full");
    gameInstance.progress = progressEl;
    loader.querySelector(".spinner").style.display = "none";
  }
  gameInstance.progress.style.transform = `scaleX(${progress})`;
  if (progress === 1) {
    loader.style.display = "none";
    gameLoaded = true;
  }
}

var gameInstance = UnityLoader.instantiate("gameContainer", gameJsonUrl, { onProgress: UnityProgress });
window.unityInstance = gameInstance;

// Prevent leaving page
window.addEventListener("beforeunload", function(e) {
  if (!gameLoaded || !lockedOccured) return null;
  var confirmationMessage = "Are you sure you want to leave?";
  (e || window.event).returnValue = confirmationMessage;
  return confirmationMessage;
});

// Pointer lock handling
document.addEventListener("pointerlockchange", function() {
  if (!lockedOccured && document.pointerLockElement) lockedOccured = true;
  if (!document.pointerLockElement && lockedOccured) {
    lockedOccured = false;
    gameInstance.SendMessage("Pause Menu", "OnCursorUnlocked");
  }
});

document.addEventListener("mozpointerlockchange", function() {
  if (!lockedOccured && document.pointerLockElement) lockedOccured = true;
  if (!document.pointerLockElement && lockedOccured) {
    lockedOccured = false;
    gameInstance.SendMessage("Pause Menu", "OnCursorUnlocked");
  }
});

// Keyboard blocking
document.onkeydown = function (e) {
  if (e.altKey || e.ctrlKey || ["F1","F2","F3","F4"].includes(e.key)) e.preventDefault();
};
document.onmouseup = function(e) { e.preventDefault(); };
